package JavaPrograms;

public class L4_Forloopexcise {

	public static void main(String[] args) 

	{
		// TODO Auto-generated method stub
		//System.out.println("test");

		
		for (int i=1;i<10;i++)
		{
			
			
			for(int j=0;j<i;j++)
			{
				System.out.print(j+1);

			}



			System.out.print("\n");
		}

	}

}


