package JavaPrograms;

public class N91_SubstringEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1="jagadeesan";
		String str2="sudhahar";
		String result=str1.substring(4); //start with left

		String result1=str1.substring(0,8);


		String result2=str1.substring(4,8);
		System.out.println(result);

		System.out.println(result1);

		System.out.println(result2);
	}

}
